<?php
    $_MIME = $_POST['typeURL'] ? preg_replace("/[^A-Za-z0-9 ]/", '', $_POST['typeURL']) : "png";
    $_DATA = $_POST['dataURL'] ? $_POST['dataURL'] : "data:image/jpeg;base64,";
    $_FILE = $_POST['fileURL'] ? $_POST['fileURL'] : "export_"+date("Ymd_his");

    # we are a PNG image
    header('Content-type: image/'.$_MIME);
     
    # we are an attachment (eg download), and we have a name
    header('Content-Disposition: attachment; filename="'.$_FILE.'.'.$_MIME.'"');
     
    #capture, replace any spaces w/ plusses, and decode
    $encoded = str_replace(' ', '+', $_DATA);
    $decoded = base64_decode($encoded);
     
    #write decoded data
    echo $decoded;
?>